mapper.insert:

insert into MOVIE_REVIEW (title, writer, director, actor, dialogue, content, recommend)values (#{title}
	, #{writer} , #{director}, #{actor}, #{dialogue}, #{content},
	#{recommend})

mapper.update:
update MOVIE_REVIEW
	set title=#{title}, writer=#{writer},
	director=#{director}, actor=#{actor}, dialogue=#{dialogue},
	content=#{content}, recommend=#{recommend}
	where seq=#{seq}
